import React, { useState } from 'react';

type UserType = 'new' | 'returning' | 'question' | null;

const BookingPage: React.FC = () => {
  const [step, setStep] = useState<number>(1);
  const [userType, setUserType] = useState<UserType>(null);
  const [formData, setFormData] = useState({
    name: '',
    phoneNumber: '',
  });

  const handleUserTypeSelect = (type: UserType) => {
    setUserType(type);
    if (type === 'question') {
      // Redirect to chatbot (to be implemented)
      window.location.href = '/chatbot';
    } else {
      setStep(2);
    }
  };

  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    // Here you would typically make an API call to save the data
    setStep(3);
  };

  const renderStep = () => {
    switch (step) {
      case 1:
        return (
          <div>
            <h2>Welcome to Our Booking System</h2>
            <button onClick={() => handleUserTypeSelect('new')}>I'm a New Customer</button>
            <button onClick={() => handleUserTypeSelect('returning')}>I'm a Returning Customer</button>
            <button onClick={() => handleUserTypeSelect('question')}>I Have a Question</button>
          </div>
        );
      case 2:
        return (
          <form onSubmit={handleSubmit}>
            {userType === 'new' && (
              <div>
                <label>Name</label>
                <input
                  type="text"
                  name="name"
                  value={formData.name}
                  onChange={handleInputChange}
                  required
                />
              </div>
            )}
            <div>
              <label>Phone Number</label>
              <input
                type="tel"
                name="phoneNumber"
                value={formData.phoneNumber}
                onChange={handleInputChange}
                required
              />
            </div>
            <button type="submit">Continue</button>
          </form>
        );
      case 3:
        return (
          <div>
            <h2>Thank You!</h2>
            <p>
              {userType === 'new'
                ? 'Your information has been received. We will contact you shortly.'
                : 'We will send you a verification code shortly.'}
            </p>
          </div>
        );
      default:
        return null;
    }
  };

  return <div>{renderStep()}</div>;
};

export default BookingPage;
