import React, { useState } from 'react';

type UserType = 'new' | 'returning' | 'question' | null;

const BookingPage: React.FC = () => {
  const [step, setStep] = useState<number>(1);
  const [userType, setUserType] = useState<UserType>(null);
  const [formData, setFormData] = useState({
    name: '',
    phoneNumber: '',
  });

  const handleUserTypeSelect = (type: UserType) => {
    setUserType(type);
    if (type === 'question') {
      window.location.href = '/chatbot';
    } else {
      setStep(2);
    }
  };

  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    setStep(3);
  };

  const renderStep = () => {
    switch (step) {
      case 1:
        return (
          <div className="max-w-md w-full mx-auto mt-16 p-8 bg-white rounded-2xl shadow-2xl border border-gray-100">
            <h2 className="text-3xl font-extrabold mb-8 text-center text-gray-800">Book Your Appointment</h2>
            <div className="space-y-4">
              <button
                onClick={() => handleUserTypeSelect('new')}
                className="w-full py-3 px-4 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition font-semibold shadow"
              >
                I'm a New Customer
              </button>
              <button
                onClick={() => handleUserTypeSelect('returning')}
                className="w-full py-3 px-4 bg-green-600 text-white rounded-lg hover:bg-green-700 transition font-semibold shadow"
              >
                I'm a Returning Customer
              </button>
              <button
                onClick={() => handleUserTypeSelect('question')}
                className="w-full py-3 px-4 bg-gray-600 text-white rounded-lg hover:bg-gray-700 transition font-semibold shadow"
              >
                I Have a Question
              </button>
            </div>
          </div>
        );
      case 2:
        return (
          <div className="max-w-md w-full mx-auto mt-16 p-8 bg-white rounded-2xl shadow-2xl border border-gray-100">
            <h2 className="text-2xl font-bold mb-8 text-center text-gray-800">
              {userType === 'new' ? 'New Customer Information' : 'Returning Customer'}
            </h2>
            <form onSubmit={handleSubmit} className="space-y-6">
              {userType === 'new' && (
                <div>
                  <label htmlFor="name" className="block text-sm font-medium text-gray-700 mb-1">
                    Name
                  </label>
                  <input
                    type="text"
                    id="name"
                    name="name"
                    value={formData.name}
                    onChange={handleInputChange}
                    required
                    className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500"
                  />
                </div>
              )}
              <div>
                <label htmlFor="phoneNumber" className="block text-sm font-medium text-gray-700 mb-1">
                  Phone Number
                </label>
                <input
                  type="tel"
                  id="phoneNumber"
                  name="phoneNumber"
                  value={formData.phoneNumber}
                  onChange={handleInputChange}
                  required
                  className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500"
                />
              </div>
              <button
                type="submit"
                className="w-full py-3 px-4 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition font-semibold shadow"
              >
                Continue
              </button>
            </form>
          </div>
        );
      case 3:
        return (
          <div className="max-w-md w-full mx-auto mt-16 p-8 bg-white rounded-2xl shadow-2xl border border-gray-100 text-center">
            <h2 className="text-2xl font-bold mb-4 text-green-600">Thank You!</h2>
            <p className="text-gray-700">
              {userType === 'new'
                ? 'Your information has been received. We will contact you shortly.'
                : 'We will send you a verification code shortly.'}
            </p>
          </div>
        );
      default:
        return null;
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 to-gray-100 flex items-center justify-center">
      {renderStep()}
    </div>
  );
};

export default BookingPage;
